easing: 'sinusoidalInOut',
scaleX: 1,
scaleY: 1
}]
}, {
// Translate animation.
duration: 2000,
loop: true,
keyframes: [{
percent: 0,
x: 10
}, {
percent: 1,
x: 100
}]
